function subcategoryNameFormatter(row, cell, value, columnDef, dataContext) {
    if (value == null) return "";

    var name;
    for (var subcat in subcategories) {
        if (value == subcategories[subcat].ProductSubcategoryID)
            return subcategories[subcat].Name;
    }
    return "";
}

function asyncSubcategoryNameFormatter(row, cell, value, columnDef, dataContext) {
    if (value == null) return "";

    // Comprobamos si el valor existe en la cache
    if (columnDef.cache[value] !== undefined)
        return columnDef.cache[value];

    return "Cargando...";
}

function getSubcatNameFromBBDD(subcategory) {
    function sleep(milliseconds) {
        var start = new Date().getTime();
        for (var i = 0; i < 1e7; i++) {
            if ((new Date().getTime() - start) > milliseconds) {
                break;
            }
        }
    }
    var name = "";
    for (var subcat in subcategories) {
        if (subcategory == subcategories[subcat].ProductSubcategoryID) {
            name = subcategories[subcat].Name;
            break;
        }
    }
    sleep(200);
    return name;
}

function getSubcategoryName(cellNode, row, dataContext, colDef) {
    var cell = $(cellNode);
    if (cell.text() !== "Cargando...") return;

    var value = dataContext[colDef.field];
    var name;
    // Comprobamos si el valor existe en la cache
    if (colDef.cache[value] === undefined) {
        name = getSubcatNameFromBBDD(value);
        // Introducimos el valor en la cache
        colDef.cache[value] = name;
    }
    else
        name = colDef.cache[value];

    $(cellNode).text(name);
}

function subcategoryEditor(args) {
    var input;
    var oldValue;

    var init = function () {
        var inputElement = document.createElement("input");
        inputElement.setAttribute("type", "text");
        inputElement.className = "gridTextEditor";
        args.container.appendChild(inputElement);
        input = $(inputElement);
        input.bind("keydown", function (e) {
            if (e.keyCode === $.ui.keyCode.LEFT || e.keyCode === $.ui.keyCode.RIGHT) {
                e.stopImmediatePropagation();
            }
        })
        .focus()
        .select();
    }();

    this.destroy = function () { input.remove(); }

    this.focus = function () { input.focus(); }

    this.getValue = function () { return input.val(); }

    this.setValue = function (value) { input.val(value); }

    this.loadValue = function (item) {
        oldValue = item[args.column.field] || "";
        input.val(oldValue).attr("defaultValue", oldValue);
        input.select();
    }

    this.serializeValue = function () { return input.val(); }

    this.applyValue = function (item, state) { item[args.column.field] = state; }

    this.isValueChanged = function () { return (input.val() != oldValue); }

    this.validate = function () {
        var value = input.val();
        var valid = (value == null || value == "");
        if (!valid) {
            for (var subcat in subcategories) {
                if (value == subcategories[subcat].ProductSubcategoryID)
                {
                    valid = true;
                    break;
                }
            }
        }
        return {
            valid: valid,
            msg: (valid ? null : "La subcategoría " + value + " no existe.")
        };
    }
}